using Xunit;

namespace AdventOfCode._2015;

public sealed class Day1
{
    public int Solve1()
    {
        int curr = 0;

        foreach (var c in Input)
        {
            if(c == '(')
            {
                curr++;
            }
            else
            {
                curr--;
            }
        }

        return curr;
    }

    public int Solve2()
    {
        int curr = 0;

        for (int i = 0; i < Input.Length; i++)
        {
            char c = Input[i];

            if (c == '(')
            {
                curr++;
            }
            else
            {
                curr--;
            }

            if(curr == -1)
            {
                return i + 1;
            }
        }

        return -1;
    }

    [Fact]
    public void Test1() => Assert.Equal(280, Solve1());

    [Fact]
    public void Test2() => Assert.Equal(1797, Solve2());

