using Xunit;

namespace AdventOfCode._2015;

public sealed class Day1
{
    public int Solve1(string input)
    {
        int level = 0;

        foreach (var c in input)
        {
            if(c == '(')
            {
                level++;
            }
            else
            {
                level--;
            }
        }

        return level;
    }

    public int Solve2(string input)
    {
        int level = 0;

        for (int i = 0; i < input.Length; i++)
        {
            char c = input[i];

            if (c == '(')
            {
                level++;
            }
            else
            {
                level--;
            }

            if(level == -1)
            {
                return i + 1;
            }
        }

        return -1;
    }

    [Fact]
    public void Test1() => Assert.Equal(280, Solve1(Input));

    [Fact]
    public void Test2() => Assert.Equal(1797, Solve2(Input));

